<div class="row">
       <div class="col-md-12 text-center"> 
         <h2>1 BTC = <?php echo $deposit['dh_btc_rate'];?> USD </h2>
         <h2>Amount to Due: <?php echo $deposit['dh_btc'];?> BTC </h2>
         <h2>Amount to Deposit: $<?php echo $deposit['dh_btcusd'];?> USD </h2>

         <div class="plane-text">Remit payment to the below address to complete your Deposit Amount.</div>
         <div id="messages"></div>

         <div class="bitcoin-wallet" id="bitcoin-wallet">
             <a target="_blank" href="https://chart.googleapis.com/chart?chs=300x300&cht=qr&chl=bitcoin:<?php echo $deposit['dh_blockchain_address']; ?>?amount=<?php echo $deposit['dh_btc']; ?>">
          <img src="http://chart.googleapis.com/chart?chs=125x125&cht=qr&chl=bitcoin:<?php echo $deposit['dh_blockchain_address']; ?>?amount=<?php echo $deposit['dh_btc']; ?>" width="50%">
             </a>
          <div class="text-center">
              <label for="">TO THIS BITCOIN (BTC) ADDRESS</label>
          <input type="text" style="width: 308px;margin: 0 auto;" class="form-control" id="payBox" onclick="copy_blockchain_address(this)" ondblclick="open_blockchain('<?php echo $deposit['dh_blockchain_address']; ?>')" value="<?php echo $deposit['dh_blockchain_address']; ?>" onclick="this.select();" readonly>
          </div>
         </div>

       </div>
</div>


<script src="assets/jquery-validation/dist/jquery.validate.js" type="text/javascript"></script>

<script>
   var current_rate = <?php echo $deposit['dh_btc_rate'];?>;

    function copy_blockchain_address(obj){
        obj.select();
        /* Copy the text inside the text field */
        document.execCommand("copy");
    }
  function open_blockchain(wallet_address) {
      var url = 'https://www.blockchain.com/btc/address/'+wallet_address;
      var win = window.open(url, '_blank');
      win.focus();
  }
  
  var btcs = new WebSocket('wss://ws.blockchain.info/inv');
  btcs.onopen = function()
  {
    btcs.send( JSON.stringify( {"op":"addr_sub", "addr":"<?php echo $deposit['dh_blockchain_address']; ?>"} ) );
    //btcs.send( JSON.stringify( {"op":"unconfirmed_sub"} ) );
  };

  btcs.onmessage = function(onmsg)
{
  var response = JSON.parse(onmsg.data);
  var getOuts = response.x.out;
  var countOuts = getOuts.length; 
  for(i = 0; i < countOuts; i++)
  {
    //check every output to see if it matches specified address
    var outAdd = response.x.out[i].addr;
    var specAdd = "<?php echo $deposit['dh_blockchain_address']; ?>";
       if (outAdd == specAdd)
       {
       var amount = response.x.out[i].value;
       var calAmount = amount / 100000000;
       
       $.get('<?php echo  base_url('payment/bitcoin_callback')."/?invoice_id=".$deposit['dh_invoice_id']."&secret=".BITCOIN_SECRET; ?>&value='+amount+'&transaction_hash='+response.x.hash,function(response_from){
        console.log(response_from);
           });
       
       $('#messages').prepend('<div class="alert alert-success">Payment Successfully Received :  ' + calAmount + " BTC</div>");
       
     var snd = new  Audio("data:audio/wav;base64,");
     snd.play();

     $('#bitcoin-wallet').hide();
     
    }
  } 
}



</script>
